// Extract file info from input
const fileId = $input.first().json.file_id;
const fileName = $input.first().json.file_name;
const mimeType = $input.first().json.mime_type;
const extension = $input.first().json.extension;

if (!fileId) {
  throw new Error('No file_id provided');
}

return [{
  json: {
    file_id: fileId,
    file_name: fileName,
    mime_type: mimeType,
    extension: extension,
    fetch_started: new Date().toISOString()
  }
}];

// Process downloaded binary content and include base64 in JSON for sub-workflow passing
const item = $input.first();
const binary = item.binary;
const fileInfo = item.json;

// Find the binary key (usually 'data' but could be anything)
const binaryKey = binary ? Object.keys(binary)[0] : null;
const binaryObj = binaryKey ? binary[binaryKey] : null;

let debugInfo = {
  hasBinary: !!binary,
  binaryKeys: binary ? Object.keys(binary) : [],
  binaryKey: binaryKey,
  binaryObjKeys: binaryObj ? Object.keys(binaryObj) : []
};

if (!binaryObj) {
  return [{
    json: {
      ...fileInfo,
      status: 'error',
      error_message: 'No binary data received',
      debug: debugInfo
    }
  }];
}

// In n8n, binary data can be accessed via helpers or directly
// Try to get the base64 data using the proper method
let base64Data = null;
let fileSize = 0;

try {
  // Method 1: Use getBinaryDataBuffer helper (recommended for n8n)
  const binaryBuffer = await this.helpers.getBinaryDataBuffer(0, binaryKey);
  base64Data = binaryBuffer.toString('base64');
  fileSize = binaryBuffer.length;
  debugInfo.method = 'getBinaryDataBuffer';
  debugInfo.bufferLength = fileSize;
} catch (e) {
  debugInfo.helperError = e.message;
  // Method 2: Try direct access to data property
  if (binaryObj.data && typeof binaryObj.data === 'string' && binaryObj.data.length > 100) {
    base64Data = binaryObj.data;
    fileSize = binaryObj.fileSize || 0;
    debugInfo.method = 'direct_data';
  }
}

if (!base64Data) {
  return [{
    json: {
      ...fileInfo,
      status: 'error',
      error_message: 'Could not extract binary data',
      debug: debugInfo
    }
  }];
}

// Include binary content as base64 IN THE JSON for reliable passing through Execute Workflow
return [{
  json: {
    file_id: fileInfo.file_id,
    file_name: fileInfo.file_name,
    mime_type: fileInfo.mime_type || binaryObj.mimeType,
    extension: fileInfo.extension,
    file_size: fileSize,
    status: 'downloaded',
    fetch_completed: new Date().toISOString(),
    binary_data: base64Data,
    binary_mime_type: binaryObj.mimeType,
    debug: debugInfo
  },
  binary: item.binary
}];
